import React from "react";
import {useEffect,useState} from "react";

function Rankings(){

    const [batsman1,setbatsman1] = useState([])
    const [batsman2,setbatsman2] = useState([])
    const [batsman3,setbatsman3] = useState([])
    const [bowler,setbowler] = useState([])
    const [allrounder,setallrounder] = useState([])
    const [teams,setteams] = useState([])
    const [loading,setLoading] = useState([])

    useEffect(() => {
        
        setLoading(true);
        const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': '27acb5ed23msh42d806119019ef1p17d5c9jsn7adca89b6e0f',
		        'X-RapidAPI-Host': 'cricbuzz-cricket.p.rapidapi.com'
            }
              
        };
  
        fetch('https://cricbuzz-cricket.p.rapidapi.com/stats/v1/rankings/batsmen?formatType=test', options)
        .then((res) => res.json())
        .then((data) => {
            setbatsman1(data);
        })
        .catch((err) => {
            console.log(err);
        })
        .finally(() => {
            setLoading(false);
        });

    },[])

    console.log(batsman1.rank);


    return(
        <div className="">
            <div className="bg-gray-700 ml-auto mr-auto w-fit basis-1/4 grow-0 shrink-0 flex text-white mt-8 shadow-xl  text-2xl">
                <button className="p-8 border border-white ml-auto">Batsman</button>
                <button className="p-8 border border-white">Bowler</button>
                <button className="p-8 border border-white">Allrounder</button>
                <button className="p-8 mr-auto border border-white">Team</button>
            </div>

            {!loading?
            
                <div>
                    <div className="bg-gray-700 text-lg text-white mt-12 h-4/6 overflow-auto no-scrollbar w-4/12">
                        <h1>Test</h1>
                        
                
                        {batsman1.rank.map(element =>{
                            return(

                                <div className="flex">
                                    <span>{element.rank}</span>
                                    <span>{element.name}</span>
                                    <span>{element.country}</span>
                                    <span>{element.rating}</span>
                                </div>


                            )
                        })}
                     

                    </div>
                </div>
                
                :<></>}

        </div>
    )

}

export default Rankings
