import React, { useEffect, useState } from "react";

function Home() {

    const [livedata, setlivedata] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        setLoading(true);
        const options = {
            method: 'GET',
            headers: {
		        'X-RapidAPI-Key': 'a5550a9c63mshd86fe6bb3e5ec17p1c5481jsnd3f63fadd3a8',
		        'X-RapidAPI-Host': 'cricbuzz-cricket.p.rapidapi.com'
	        }
        };
  
    fetch('https://cricbuzz-cricket.p.rapidapi.com/matches/v1/live', options)
        .then((res) => res.json())
        .then((data) => {
            setlivedata(data);
        })
        .catch((err) => {
            console.log(err);
        })
        .finally(() => {
            setLoading(false);
        });
    }, []);


   const finaldata = [];

    
   if(!loading)
   {

        
        const flivedata = livedata.typeMatches.filter((element) =>{

            return (element.matchType === 'International' || element.matchType === 'League')

        });


        flivedata.forEach(element => {
            
         
           element.seriesMatches.forEach((element) => {
            
            if(element.seriesAdWrapper)
            {
                finaldata.push(element.seriesAdWrapper);
            }


           })

        
        });

        console.log("trial",finaldata);

        return(
            <div className=" bg-gray-700 p-8 w-full">
                {finaldata.map(element => {

                    return(
                        <div className="">
                            <div className="w-12/12 mx-20">
                            {element.seriesName}
                            </div>

                            <div>
                                {element.matches.forEach(element =>{
                                    
                                    return(
                                        <div className="w-12/12 mx-20">
                                            {element.matchInfo.team1.teamName}
                                            {/* {element.matchInfo.team2.teamName} */}
                                        </div>
                                    )
                                    
                                })}
                            </div>
                         
                            
                        </div>
                    )
                    
                })}
            </div>
        )
      
   }

  
        
  

   

}


export default Home;
