import React from "react";
import {useEffect,useState} from "react";

function Schedule(){

    const [loading,setloading] = useState(true);
    const [data,setdata] = useState([]);

    const options = {
        headers: {
            'X-RapidAPI-Key': 'a5550a9c63mshd86fe6bb3e5ec17p1c5481jsnd3f63fadd3a8',
            'X-RapidAPI-Host': 'cricbuzz-cricket.p.rapidapi.com'
        } 
    }

    useEffect(() => {

        setloading(true);
   
        fetch('https://cricbuzz-cricket.p.rapidapi.com/schedule/v1/international', options)
        .then((res) => res.json())
        .then((data) => {
            setdata(data);
        })
        .catch((err) => {
            console.log(err);
        })
        .finally(() => {
            setloading(false);
        });
      
    }, [])

    const fdata = [];

    if(!loading)
    {
        data.matchScheduleMap.forEach((element) =>{

            if(element.scheduleAdWrapper)
            fdata.push(element.scheduleAdWrapper)
        })
    }

    console.log(fdata);

    return(
        <div className="h-5/6 bg-gray-100 overflow-auto no-scrollbar">
            {fdata.map((element) =>{

                return(
                    <div className="bg-gray-800 h-1/6 text-white mt-4 mx-4 p-4 shadow-2xl flex flex-row overflow-auto no-scrollbar content-center ">
                        <div className="text-2xl h-2/6 text-center">
                        {element.date}
                        </div>

                        {element.matchScheduleList.map((element)=>

                           
                            <div className="text-2xl h-2/6 text-center">
                            {element.seriesName}
                            </div>
                            
                            // console.log(element.seriesName)
                        
                        )}

                    </div>
                )
            })}

        </div>
    )

}



export default Schedule;
